a='''<!DOCTYPE html><html lang=\\"zh\\"><head><meta charset=\\"UTF-8\\"><title>幸运转盘点名器</title><style>html, body {margin: 0;padding: 0;height: 100%;background: linear-gradient(135deg, #fefcea, #f1da36);display: flex;justify-content: center;align-items: center;flex-direction: column;font-family: \\"微软雅黑\\", sans-serif;}h1 {font-size: 3vw;margin: 20px;text-align: center;color: #333;}canvas {max-width: 90vw;max-height: 60vh;width: 100%;height: auto;}button {font-size: 1.2rem;padding: 0.6em 1.5em;margin-top: 20px;background-color: #f76c6c;color: white;border: none;border-radius: 10px;cursor: pointer;}button:hover {background-color: #d94a4a;}#result {margin-top: 20px;font-size: 1.8rem;color: green;text-align: center;}</style></head><body><h1>🎡 幸运点名转盘 🎡</h1><canvas id=\\"wheel\\"></canvas><button id=\\"startBtn\\">开始点名</button><div id=\\"result\\"></div><script>const students = [\\"张刚\\",\\"苏凤兰\\",\\"孙浩\\",\\"杜明\\",\\"刘博\\",\\"闵玉梅\\",\\"曾明\\",\\"郭秀云\\",\\"吴秀荣\\",\\"尚荣\\",\\"华帅\\",\\"郑志强\\",\\"冯桂香\\",\\"佟海燕\\",\\"卢娟\\",\\"赵玉兰\\",\\"杜岩\\",\\"吴桂芳\\",\\"吴金凤\\",\\"田丽娟\\",\\"文洋\\",\\"邹玉\\",\\"吕秀英\\"];const colors = [\\"#f94144\\", \\"#f3722c\\", \\"#f8961e\\", \\"#f9844a\\", \\"#f9c74f\\", \\"#90be6d\\", \\"#43aa8b\\", \\"#577590\\"];const canvas = document.getElementById(\'wheel\');const ctx = canvas.getContext(\'2d\');let angle = 0;let isSpinning = false;let currentRotation = 0;function resizeCanvas() {canvas.width = Math.min(window.innerWidth * 0.9, window.innerHeight * 0.6);canvas.height = canvas.width;drawWheel();}window.addEventListener(\'resize\', resizeCanvas);resizeCanvas();function drawWheel() {const num = students.length;const angleStep = (2 * Math.PI) / num;const radius = canvas.width / 2;const center = radius;ctx.clearRect(0, 0, canvas.width, canvas.height);for (let i = 0; i < num; i++) {const startAngle = angle + i * angleStep;const endAngle = startAngle + angleStep;ctx.beginPath();ctx.moveTo(center, center);ctx.arc(center, center, radius, startAngle, endAngle);ctx.fillStyle = colors[i % colors.length];ctx.fill();ctx.stroke();ctx.save();ctx.translate(center, center);ctx.rotate(startAngle + angleStep / 2);ctx.textAlign = \\"right\\";ctx.fillStyle = \\"white\\";ctx.font = `${radius * 0.08}px 微软雅黑`;ctx.fillText(students[i], radius - 10, 5);ctx.restore();}}function spinWheel() {if (isSpinning) return;isSpinning = true;let spinAngle = Math.random() * 360 + 360 * 5;let duration = 4000;let start = null;function animate(timestamp) {if (!start) start = timestamp;const progress = timestamp - start;const ease = (t) => 1 - Math.pow(1 - t, 3);let easedProgress = ease(progress / duration);let deg = spinAngle * easedProgress;currentRotation = deg;angle = deg * Math.PI / 180;drawWheel();ctx.save();ctx.translate(canvas.width / 2, canvas.height / 2);ctx.rotate(angle);ctx.translate(-canvas.width / 2, -canvas.height / 2);drawWheel();ctx.restore();if (progress < duration) {requestAnimationFrame(animate);} else {isSpinning = false;showResult();}}requestAnimationFrame(animate);}function showResult() {const totalDegrees = currentRotation % 360;const anglePer = 360 / students.length;let index = Math.floor((360 - totalDegrees + anglePer / 2) % 360 / anglePer);let winner = students[index];document.getElementById(\\"result\\").innerHTML = `🎉 恭喜 <b>${winner}</b> 被点名啦！`;}document.getElementById(\\"startBtn\\").onclick = spinWheel;</script></body></html>'''
a=a.replace('\\','')
